mapper.select:
select u.id,u.username,u.login_name,
       r.id as roleId,r.name as roleName,
       p.id as permissionId,p.name as permissionName,p.code as permissionCode
from t_sys_user u
         left join t_sys_user_role ur on u.id=ur.user_id
         left join t_sys_role r on r.id=ur.role_id
         left join t_sys_role_permission rp on rp.role_id=ur.role_id
         left join t_sys_permission p on p.id=rp.permission_id


                 u.id=#{userId}


                 r.id=#{roleId}



                 p.name like concat('%',#{name},'%')


                 p.code=#{code}



ORDER BY u.username,r.name,p.name;

select p.*
from t_sys_permission p
left join t_sys_role_permission rp on rp.permission_id=p.id
left join t_sys_user_role ur on ur.role_id=rp.role_id


        ur.user_id=#{userId}



        ur.role_id=#{roleId}


ORDER BY p.name;
