mapper.select:
select u.id,u.username,u.login_name,
       r.id as roleId,r.name as roleName
from t_sys_user u
         left join t_sys_user_role ur on u.id=ur.user_id
         left join t_sys_role r on r.id=ur.role_id
where

    r.id=#{id}


    u.id=#{userId}

ORDER BY u.username,r.name;

select r.*
from t_sys_role r
left join t_sys_user_role ur on r.id=ur.role_id
where

    r.id=#{id}


    ur.user_id=#{userId}

ORDER BY r.name;
